import React, {Component} from 'react';
import { DataHelper } from '../../utils/DataHelper';
import { Multiselect } from 'multiselect-react-dropdown';

class RoleSelect extends Component {
    constructor(props) {
        super(props);
    }

  render() {
      return (
        <Multiselect options={DataHelper.getRoleData()} selectedValues={this.props.selectedValues} displayValue="name" placeholder="Roles" hidePlaceholder="true" onSelect={this.props.onSelectHandler} onRemove={this.props.onRemoveHandler}></Multiselect>
    );
  }

}

export default RoleSelect;
