import React, {Component} from 'react';
import './BuildList.css';
import './BuildListItem.css';
import BuildListItem from './BuildListItem';
import SimpleBar from 'simplebar-react';
import 'simplebar/dist/simplebar.min.css';
import { db } from '../../utils/Firebase';

// accept a userid, if no id provided get all builds with page limits
// if an id is passed, get the builds for this user only

class BuildList extends Component {
  constructor(props) {
    super(props);

  }

  loadBuildData() {
    if (this.props.userId) {
      console.log('loading builds from db for user id');
      console.log(this.props.userId);

      db.collection('builds').where("userId", "==", this.props.userId).get().then((querySnapshop) => {
        return querySnapshop;
      }).catch((error) => {
        console.error('Error getting builds from DB', error);
      });
    }

  }

  render() {
    this.loadBuildData();

    return (
          <div className="build-list border-01 background-16">
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
              <BuildListItem></BuildListItem>
            {this.renderBuilds()}
          </div>
    );
  }

  renderBuilds() {
    var builds = loadBuildData();
    builds.forEach((doc) => {
      console.log(doc.id);
      console.log(doc.data());
    });
  }
}

export default BuildList;
