import React, {Component} from 'react';
import './BuildList.css';
import './BuildListItem.css';
import BuildListItem from './BuildListItem';
import SimpleBar from 'simplebar-react';
import 'simplebar/dist/simplebar.min.css';
import { db } from '../../utils/Firebase';

// accept a userid, if no id provided get all builds with page limits
// if an id is passed, get the builds for this user only

class BuildList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      builds: {}
    };
  }

  loadBuildData() {

    if (this.props.userId) {
      console.log('loading builds from db for user id');
      console.log(this.props.userId);

      db.collection('builds').where("userId", "==", this.props.userId).get().then((querySnapshop) => {
        var buildHtml = [];
        querySnapshop.forEach((doc) => {
          console.log(doc.id);
          console.log(doc.data());
          buildHtml.push(this.renderBuildListItem(doc));
        });
        console.log(buildHtml);
        this.state.builds = querySnapshop;
        return buildHtml;
      }).catch((error) => {
        console.error('Error getting builds from DB', error);
      });
    }

  }

  renderBuildListItem(buildDoc) {
    return <div className="build-list-item border-02 background7">
              <div className={`build-hero-icon hero-icon-${buildDoc.data().careerId} border-04`}></div>
              <p className="build-name">Yer Best Friend Kruber</p>
              <div className="build-description-container">
                  <p className="build-hero">Mercenary</p>
                  <p className="build-author">B-Fir3</p>
              </div>
              <p></p>
              <div className="talents">
                <div className={`talent-icon talent-${buildDoc.data().careerId}0${buildDoc.data().talents1} talent-2`}></div>
                <div className={`talent-icon talent-${buildDoc.data().careerId}0${buildDoc.data().talents2} talent-2`}></div>
                <div className={`talent-icon talent-${buildDoc.data().careerId}0${buildDoc.data().talents3} talent-2 `}></div>
                <div className={`talent-icon talent-${buildDoc.data().careerId}${buildDoc.data().talents4} talent-2`}></div>
                <div className={`talent-icon talent-${buildDoc.data().careerId}${buildDoc.data().talents5} talent-3`}></div>
                <div className={`talent-icon talent-${buildDoc.data().careerId}${buildDoc.data().talents6} talent-1`}></div>
              </div>
              <div className="weapons">
                <div className="weapon-icon weapon-01 weapon-background border-10"></div>
                <div className="trait-icon trait-parry border-10"></div>
                <div className="weapon-icon weapon-02 weapon-background border-10"></div>
                <div className="trait-icon trait-conservative-shooter border-10"></div>
              </div>
              <div className="traits">
                <div className="trait-icon trait-barkskin border-10"></div>
                <div className="trait-icon trait-proxy border-10"></div>
                <div className="trait-icon trait-shrapnel border-10"></div>
              </div>
              <div className="build-footer">
                  <p className="patch-number">Tank - Legend - v2.0.8</p>
                  <div></div>
              </div>
            </div>;
  }

  render() {

    console.log('rendering build list ');
    console.log(this.state.builds);
    if (!this.state.builds) {
      this.loadBuildData();
    }

    return (
          <div className="build-list border-01 background-16">
            {this.renderBuilds()}
          </div>
    );
  }

  renderBuilds() {
    return <BuildListItem></BuildListItem>;
  }
}

export default BuildList;
