import React, {Component} from 'react';
import { BuildPageContext } from '../../stores/BuildPageStore';
import { AppContext } from '../../stores/Store';
import { Multiselect } from 'multiselect-react-dropdown';
import './BuildOptions.css';

class BuildOptions extends Component {
    static contextType = AppContext;
  render() {
    const [state, updateState] = this.context;

    let rolesData = [ 
        {
            name: 'Frontliner',
            value: 0
        },
        {
            name: 'Backliner',
            value: 0
        },
        {
            name: 'Horde',
            value: 0
        },
        {
            name: 'Boss',
            value: 0
        },
        {
            name: 'Elite',
            value: 0
        },
        {
            name: 'Special',
            value: 0
        },
        {
            name: 'Tank',
            value: 0
        },
        {
            name: 'CC',
            value: 0
        },
        {
            name: 'Support',
            value: 0
        },
        {
            name: 'Solo',
            value: 0
        }
    ];

    return (
        <div className="build-options-container">
                                <select value={state.difficulty}>
                                    <option hidden defaultValue>Difficulty</option>
                                    <option value="0">Any</option>
                                    <option>Recruit</option>
                                    <option>Veteran</option>
                                    <option>Champion</option>
                                    <option>Legend</option>
                                    <option>Catacylsm</option>
                                </select>
                                <select value={state.mission}>
                                    <option hidden defaultValue>Mission</option>
                                    <option value="0">Any</option>
                                    <option>Helmgart</option>
                                    <option>Weave 1</option>
                                </select>
                                <select value={state.potion}>
                                    <option hidden defaultValue>Potion Preference</option>
                                    <option value="0">Any</option>
                                    <option>Strength Potion</option>
                                    <option>Speed Potion</option>
                                    <option>Concentration Potion</option>
                                </select>
                                <Multiselect className="select-list" options={rolesData} displayValue="name"></Multiselect>
                            </div>
        );
      }
    }
    
    export default BuildOptions;
