import React from 'react'
import { AppContext } from './Store';

const BuildsListContext = React.createContext();

export {BuildsListContext}

function reducer(state, action) {
    switch(action.type) {
        case 'UPDATE_CAREER':
            return {...state, careerId: action.payload.careerId};
        default:
            throw new Error('Error updating Build List state.');
    }
}

export default function BuildsListStore(props) {

    const stateHook = React.useReducer(reducer, {
        careerId: 0,
        userId: '',
        role: '',
        difficulty: '',
        mission: '',
        potion: '',
        sortBy: '',
        patch: '',
        meleeId: 0,
        rangeId: 0,
        meleeTraitId: 0,
        rangeTraitId: 0,
        necklaceTraitId: 0,
        charmTraitId: 0,
        trinketTraitId: 0
    });

    return (
        <AppContext.Provider value={stateHook}>
            {props.children}
        </AppContext.Provider>
    )
}
