import {heroesData} from '../data/Heroes'

export class DataHelper {
    static getCareer = (careerId) => {
        for (var i = 0; i < heroesData.length; i++) {
            if (heroesData[i].id === parseInt(careerId)) {
              return heroesData[i];
            }
          }
        return null;
    }
}
