import React, {Component} from 'react';
import { DataHelper } from '../../utils/DataHelper';
import { Multiselect } from 'multiselect-react-dropdown';
import { AppContext } from '../../stores/Store';

class DifficultySelect extends Component {
  static contextType = AppContext;

    constructor(props) {
        super(props);
    }

  render() {
      let onSelectHandler = this.props.onSelectHandler ? this.props.onSelectHandler : this.difficultySelectChange;

      alert(this.props.onSelectHandler);


      return (
        <Multiselect className="test" closeIcon="cancel" closeOnSelect="false"  options={DataHelper.getDifficultyData()} selectedValues={this.props.selectedValues} displayValue="name" placeholder="Difficulty" hidePlaceholder="true" onSelect={onSelectHandler.bind(this)} onRemove={this.props.onRemoveHandler}></Multiselect>
    );
  }

  difficultySelectChange(selectedList, selectedItem)  {

    alert('difficulty changed');
    const [state, updateState] = this.context;

    updateState({
        type: "UPDATE_DIFFICULTY", 
        payload: selectedList
    });
  }

}

export default DifficultySelect;
