import React, {Component} from 'react';
import './BuildList.css';
import './BuildListItem.css';
import '../../assets/css/Talents.css';
import HeroTalentIcon from '../heroTalents/HeroTalentIcon';
import { Link } from 'react-router-dom';
import { DataHelper } from '../../utils/DataHelper';
import WeaponIcon from '../inventory/WeaponIcon';
import TraitIcon from '../traits/TraitIcon';


class BuildListItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      buildId: props.buildId,
      buildData: props.buildData
    }
  }
  
  render() {
    var buildModifiedDate = new Date(this.state.buildData.dateModified.seconds * 1000);
    
    var careerData = DataHelper.getCareer(this.state.buildData.careerId);
    var rangeType = (this.state.buildData.careerId === 6 || this.state.buildData.careerId === 16) ? "melee" : "range";
    var patchData = DataHelper.getPatchFromDate(buildModifiedDate);

    return (
      <Link to={'/build/' + this.state.buildId + '/view'}>
        <div className="build-list-item border-02 background7">
          <div className={`build-hero-icon hero-icon-${this.state.buildData.careerId} border-04`}></div>
          <p className="build-name">{this.state.buildData.name}</p>
          <div className="build-description-container">
              <p className="build-hero">{careerData.name}</p>
              <Link to={`/user/${this.state.buildData.userId}'/view`} className="build-author">{this.state.buildData.username}</Link>
          </div>
          <p></p>
          <div className="talents">
            <HeroTalentIcon talentNumber={this.state.buildData.talent1} tier='1' careerId={this.state.buildData.careerId}></HeroTalentIcon>
            <HeroTalentIcon talentNumber={this.state.buildData.talent2} tier='2' careerId={this.state.buildData.careerId}></HeroTalentIcon>
            <HeroTalentIcon talentNumber={this.state.buildData.talent3} tier='3' careerId={this.state.buildData.careerId}></HeroTalentIcon>
            <HeroTalentIcon talentNumber={this.state.buildData.talent4} tier='4' careerId={this.state.buildData.careerId}></HeroTalentIcon>
            <HeroTalentIcon talentNumber={this.state.buildData.talent5} tier='5' careerId={this.state.buildData.careerId}></HeroTalentIcon>
            <HeroTalentIcon talentNumber={this.state.buildData.talent6} tier='6' careerId={this.state.buildData.careerId}></HeroTalentIcon>
          </div>
          <div className="weapons">
            <WeaponIcon id={this.state.buildData.meleeId} type="melee"></WeaponIcon>
            <TraitIcon id={this.state.buildData.meleeTrait} type="melee"></TraitIcon>
            <WeaponIcon id={this.state.buildData.rangeId} type={rangeType}></WeaponIcon>            
            <TraitIcon id={this.state.buildData.rangeTrait} type={rangeType}></TraitIcon>
          </div>
          <div className="traits">
            <TraitIcon id={this.state.buildData.necklaceTrait} type="necklace"></TraitIcon>
            <TraitIcon id={this.state.buildData.charmTrait} type="charm"></TraitIcon>
            <TraitIcon id={this.state.buildData.trinketTrait} type="trinket"></TraitIcon>
          </div>
          <div className="build-footer">
              <p>{this.state.buildData.roles.join(' / ')} -</p>
              <p className="patch-number">Patch {patchData.number}</p>
              <div></div>
          </div>
        </div>
      </Link>
    );
  }
}

export default BuildListItem;
