import React, {Component, useEffect, useRef} from 'react';
import './BuildList.css';
import './BuildListItem.css';
import BuildListItem from './BuildListItem';
import 'simplebar/dist/simplebar.min.css';
import BuildsListStore from '../../stores/PagedBuildListStore';
import { db } from '../../utils/Firebase';
import { AppContext } from '../../stores/Store';
import { DataHelper } from '../../utils/DataHelper';

// accept a userid, if no id provided get all builds with page limits
// if an id is passed, get the builds for this user only

class BuildsList extends Component {
  constructor(props) {
    super(props);


    this.state = {
      builds: [],
      careerId: 0,
      isDataLoaded: false,
      isLoadingData: false,
      filters: []
    };
  }

  //current page index and total pages from props

  //specify the type of build list... or make specific types of build lists...
  //filters based on properties: userid, careerid, patch

  //static contextType = AppContext;


  //accept props: 
  //filters
  //current page number
  //last build document

  render() {
    //get filters from props
    //alert('rendering builds list');
    console.log(this.state);

    if (!this.state.isDataLoaded ||
        this.state.careerId !== this.props.careerId) {

      this.state.isLoadingData = true;

      let buildList = [];
  
      let buildsQuery = db.collection("builds");
  
      let filters = [];

      if (this.props.careerId) {
        console.log('builds list received career id ' + this.props.careerId);
        filters.push({ field: 'careerId', comparison: '==', value: this.props.careerId });
      }
  
      if (this.props.filters) {
        filters = this.props.filters;
      }
  
      filters.forEach((filter) => {
        buildsQuery = buildsQuery.where(filter.field, filter.comparison, filter.value);
      });
  
      buildsQuery.limit(10).get().then((querySnapshot) => {
        querySnapshot.forEach((build) => {
          buildList.push({ id: build.id, data: build.data()});
        });
        console.log('updating builds list state');
        console.log(buildList);
        this.setState({builds: buildList, careerId: this.props.careerId, isDataLoaded: true, isLoadingData: false})
      });
    }

    if (this.state.isLoadingData) {
      console.log('builds list data is still loading');
      
      return (
        <div data-page-number={1} data-last-page={false} className="build-list border-01 background-16">
          <span class="build-list-header header-underline">List</span>
          {/* <button className="build-list-page-button" onClick={this.props.handleNextPage}>Next Page</button> */}
        </div>
        );
    }

    return (
          <div data-page-number={1} data-last-page={false} className="build-list border-01 background-16">
            {this.renderFilters(this.state.filters)}
            <span class="build-list-header header-underline">List</span>
            {/* <button className="build-list-page-button" onClick={this.props.handleNextPage}>Next Page</button> */}
            {this.renderBuilds(this.state.builds)}
          </div>
    );
  }

  renderFilters(filters) {
    let filtersHtml = [];



    return filtersHtml;
  }

  renderBuilds(builds) {
    console.log('list of builds to render');
    console.log(builds);
    if (!builds || builds.length === 0) {
      return <p>No builds in database.</p>;
    }
    var buildsHtml = [];
    builds.forEach((build) => {
      buildsHtml.push(<BuildListItem key={build.id} buildId={build.id} buildData={build.data}></BuildListItem>)
    });
    return buildsHtml;
  }
}

export default BuildsList;
