import React from 'react'
import { AppContext } from './Store';

const UserPageContext = React.createContext();

export {UserPageContext}

function reducer(state, action) {
    switch(action.type) {
        case 'UPDATE_USER_INFO':
            return {...state, username: action.payload.username, email: action.payload.email};
        default:
            throw new Error('Error updating User Page state.');
    }
}

export default function UserPageStore(props) {

    const stateHook = React.useReducer(reducer, {
        username: '',
        email: '',
        steam: '',
        twitch: '',
        reddit: ''
    });

    return (
        <AppContext.Provider value={stateHook}>
            {props.children}
        </AppContext.Provider>
    )
}
