import React, {Component} from 'react';
import { BuildPageContext } from '../../stores/BuildPageStore';
import { AppContext } from '../../stores/Store';
import { Multiselect } from 'multiselect-react-dropdown';
import './BuildOptions.css';

class BuildOptions extends Component {
    static contextType = AppContext;

    constructor(props) {
      super(props);
      this.difficultySelectChange = this.difficultySelectChange.bind(this);
      this.missionSelectChange = this.missionSelectChange.bind(this);
      this.potionSelectChange = this.potionSelectChange.bind(this);
    }
    
  render() {
    const [state, updateState] = this.context;

    const rolesData = [ 
        {
            name: 'Frontliner',
            value: 0
        },
        {
            name: 'Backliner',
            value: 0
        },
        {
            name: 'Horde',
            value: 0
        },
        {
            name: 'Boss',
            value: 0
        },
        {
            name: 'Elite',
            value: 0
        },
        {
            name: 'Special',
            value: 0
        },
        {
            name: 'Tank',
            value: 0
        },
        {
            name: 'CC',
            value: 0
        },
        {
            name: 'Support',
            value: 0
        },
        {
            name: 'Solo',
            value: 0
        }
    ];

    const missionData = [
        { value: 'Any', label: 'Any' },
        { value: 'Against the Grain', label: 'Against the Grain' },
        { value: 'Legend', label: 'Legend' },
        { value: 'Champion', label: 'Champion' },
        { value: 'Veteran', label: 'Veteran' },
        { value: 'Recruit', label: 'Recruit' },
        { value: '[Modded]', label: 'Modded' }
    ];

    return (
        <div className="build-options-container">
                                <select value={state.difficulty} onChange={this.difficultySelectChange}>
                                    <option selected disabled hidden>Difficulty</option>
                                    {this.getDifficultyOptions()}
                                </select>
                                <select value={state.mission}>
                                    <option hidden defaultValue>Mission</option>
                                    {this.getMissionOptions()}
                                </select>
                                <select value={state.potion}>
                                    <option hidden defaultValue>Potion Preference</option>
                                    {this.getPotionOptions()}
                                </select>
                                <Multiselect options={rolesData} displayValue="name" placeholder="Add Roles" hidePlaceholder="true"></Multiselect>
                            </div>
        );
      }

      getDifficultyOptions() {
          
        let difficultyData = [
            { value: 'Any', label: 'Any' },
            { value: 'Cataclysm', label: 'Cataclysm' },
            { value: 'Legend', label: 'Legend' },
            { value: 'Champion', label: 'Champion' },
            { value: 'Veteran', label: 'Veteran' },
            { value: 'Recruit', label: 'Recruit' },
            { value: '[Modded]', label: 'Modded' }
        ];

        let html = [];
        difficultyData.forEach(difficulty => {
            html.push(<option value={difficulty.value}>{difficulty.label}</option>);
        });
        return html;
      }

      difficultySelectChange(e) {
        const [state, updateState] = this.context;
        console.log(e.currentTarget);
        console.log(e.target.value);

        updateState({
            type: "UPDATE_DIFFICULTY", 
            payload: e.target.value
        });
      }
    
    

    getMissionOptions() {          
        let missionData = [
            { value: 'Any', label: 'Any' },
            { value: 'Against the Grain', label: 'Against the Grain' },
            { value: 'Legend', label: 'Legend' },
            { value: 'Champion', label: 'Champion' },
            { value: 'Veteran', label: 'Veteran' },
            { value: 'Recruit', label: 'Recruit' },
            { value: '[Modded]', label: 'Modded' }
        ];

        let html = [];
        missionData.forEach(difficulty => {
            html.push(<option value={difficulty.value}>{difficulty.label}</option>);
        });
        
        return html;
      }

      missionSelectChange(e) {
        const [state, updateState] = this.context;
        console.log(e.currentTarget);
        console.log(e.target.value);

        updateState({
            type: "UPDATE_MISSION", 
            payload: e.target.value
        });
      }
    
    

    getPotionOptions() {
         
        let potionData = [ 
            { value: 'Any', label: 'Any' },
            { value: 'Strength', label: 'Strength' },
            { value: 'Speed', label: 'Speed' },
            { value: 'Concentration', label: 'Concentration' }
        ];

        let html = [];
        potionData.forEach(difficulty => {
            html.push(<option value={difficulty.value}>{difficulty.label}</option>);
        });
        
        return html;
      }

      potionSelectChange(e) {
        const [state, updateState] = this.context;
        console.log(e.currentTarget);
        console.log(e.target.value);

        updateState({
            type: "UPDATE_POTION", 
            payload: e.target.value
        });
      }
    
    

    getDifficultyOptions() {
          
        let difficultyData = [
            { value: 'Any', label: 'Any' },
            { value: 'Cataclysm', label: 'Cataclysm' },
            { value: 'Legend', label: 'Legend' },
            { value: 'Champion', label: 'Champion' },
            { value: 'Veteran', label: 'Veteran' },
            { value: 'Recruit', label: 'Recruit' },
            { value: '[Modded]', label: 'Modded' }
        ];

        let html = [];
        difficultyData.forEach(difficulty => {
            html.push(<option value={difficulty.value}>{difficulty.label}</option>);
        });
        
        return html;
      }

      difficultySelectChange(e) {
        const [state, updateState] = this.context;
        console.log(e.currentTarget);
        console.log(e.target.value);

        updateState({
            type: "UPDATE_DIFFICULTY", 
            payload: e.target.value
        });
      }
    }
    
    export default BuildOptions;
