import React, {Component} from 'react';
import { auth } from '../../utils/Firebase';
import './RegisterPage.css';

class RegisterPage extends Component {
  render() {
    var root = document.getElementById('root');
    root.className = '';
    root.classList.add('registerPage');

    return (
        <div className="register-page">
          <form>
            <input type="text" id="registerUsername" className="border-03" placeholder="E-MAIL"></input>
            <input type="text" id="loginEmail" className="border-03" placeholder="E-MAIL"></input>
            <input type="password" id="loginPassword" className="border-03" placeholder="PASSWORD"></input>
            <input type="password" id="loginPasswordVerify" className="border-03" placeholder="VERIFY PASSWORD"></input>
            <button id="loginButton" className="app-navigation-button border-04" onClick={this.loginUser.bind(this)}>Login</button>
            <button className="app-navigation-button border-04" data-pagename="registerPage"><Link to="/register">Register</Link></button>
          </form>
        </div>
    );
  }

  registerUser(e) {
    e.preventDefault();
    alert('Registering user!');

    let username = document.getElementById('registerUsername').value;
		let email = document.getElementById('registerEmail').value;
		let password = document.getElementById('loginPassword').value;
		let passwordVerify = document.getElementById('loginPasswordVerify').value;
		
		if (password != passwordVerify) {
			alert("Passwords don't match");
			return;ccccccccccccccccccccccccccccccccccc
		}
		
		if (!username || username.length < 3) {
			alert("Username must be at least 3 characters");
			return;
		}
		
		auth.createUserWithEmailAndPassword(email, password).then(function() {
			let user = auth.currentUser;
			
			if (!user) {
				console.log("could not update user information");
				return;
			}
			
			user.updateProfile({
			  displayName: username,
			}).then(function() {
				console.log("User information updated");
				$(".userButton").html(`${username} logout`);				
			}).catch(function(error) {
				console.log("Could not update user information");
			});
					
			$(".mainGrid").addClass("loggedIn");
			$(".userWindow").hide();
		}).catch(function(error) {
		  // Handle Errors here.
		  var errorCode = error.code;
		  var errorMessage = error.message;
		  // ...
		  alert(`${errorCode} - ${errorMessage}`);
		  return;
		});
  }
}

export default RegisterPage;
