import React, { useContext, useState } from 'react';
import { UserContext } from '../../stores/UserStore';
import { UserViewContext } from '../../stores/UserViewStore';
import { db } from '../../utils/Firebase';
import './UserPage.css';

function UserPage(props) {
    const [state, updateState] = useContext(UserViewContext);

    var root = document.getElementById('root');
    root.dataset.pageName = 'userPage';
    console.log('rendering user page for uid ' + props.userId);

    let user = {};
    db.collection('users').doc(props.userId).get().then((doc) => {
      console.log("Document data:", doc.data());
      user = doc.data();

      updateState({
        type: "UPDATE_USER_INFO", 
        payload: { 
          userId: props.userId,
          username: doc.data().username,
          steam: doc.data().steam,
          twitch: doc.data().twitch,
          dateCreated: doc.data().dateCreated,
          dateModified: doc.data().dateModified
        }
      });
    });

    return (
      <div className="user-page">
        <div className="user-info-container">
        <span type="text" value={state.username}></span>
        <span type="text" value={state.steam}></span>
        <span type="text" value={state.twitch}></span>
        <span type="text" value={new Date(state.dateCreated.seconds * 1000).toString()}></span>
        <span type="text" value={new Date(state.dateModified.seconds * 1000).toString()}></span>
        </div>
      </div>
    );
  }

export default UserPage;
