import React, {Component} from 'react';
import { DataHelper } from '../../utils/DataHelper';

class RoleSelect extends Component {
    constructor(props) {
        super(props);
    }

  render() {
      return (
        <Multiselect options={rolesData} selectedValues={DataHelper.renderListOptions(DataHelper.getRoleData())} displayValue="name" placeholder="Role" hidePlaceholder="true" onSelect={this.roleSelected} onRemove={this.roleRemoved}></Multiselect>
    );
  }

}

export default RoleSelect;
