import React, {Component} from 'react';
import { DataHelper } from '../../utils/DataHelper';

class PotionSelect extends Component {
    constructor(props) {
        super(props);
    }

  render() {
      return (
        <select value={this.props.selectedValue} onChange={this.props.onChangeHandler}>
            <option hidden defaultValue>Potion</option>
            {DataHelper.renderListOptions(DataHelper.getPotionData())}
        </select>
    );
  }

}

export default PotionSelect;
