import React, { useContext, useState } from 'react';
import { UserContext } from '../../stores/UserStore';
import { db } from '../../utils/Firebase';
import './UserPage.css';

function UserPage(props) {
    const [state, updateState] = useContext(UserContext);

    var root = document.getElementById('root');
    root.dataset.pageName = 'userPage';
    console.log('rendering user page for uid ' + props.userId);

    let user = {};
    db.collection('users').doc(props.userId).get().then((doc) => {
      console.log("Document data:", doc.data());
      user = doc.data();

      //update userPagestate with info so it refreshs
    });

    return (
      <div className="user-page">
        <input type="text" value={state.username}></input>
        <input type="text" value={user.steam}></input>
        <input type="text" value={state.twitch}></input>
        <input type="text" value={new Date(state.dateCreated.seconds * 1000).toString()}></input>
        <input type="text" value={new Date(state.dateModified.seconds * 1000).toString()}></input>
      </div>
    );
  }

export default UserPage;
