import React from 'react'
import { AppContext } from './Store';

const BuildListPageContext = React.createContext();

export {BuildListPageContext}

function reducer(state, action) {
    switch(action.type) {
        case 'UPDATE_CAREER':
            return {...state, careerId: action.payload.careerId};
        default:
            throw new Error('Error updating Build List Page state.');
    }
}

export default function BuildListPageStore(props) {

    const stateHook = React.useReducer(reducer, {
        careerId: 0,
        meleeId: 0,
        meleeProperty1: 0,
        meleeProperty2: 0,
        meleeTrait: 0,
        rangeId: 0,
        rangeProperty1: 0,
        rangeProperty2: 0,
        rangeTrait: 0,
        necklaceProperty1: 0,
        necklaceProperty2: 0,
        necklaceTrait: 0,
        charmProperty1: 0,
        charmProperty2: 0,
        charmTrait: 0,
        trinketProperty1: 0,
        trinketProperty2: 0,
        trinketTrait: 0,
        talent1: 0,
        talent2: 0,
        talent3: 0,
        talent4: 0,
        talent5: 0,
        talent6: 0,
        name: '',
        description: '',
        difficulty: '',
        mission: '',
        potion: '',
        roles: [],
        patch: '',
        userId: '',
        username: ''
    });

    return (
        <AppContext.Provider value={stateHook}>
            {props.children}
        </AppContext.Provider>
    )
}
