import React, {Component} from 'react';
import { DataHelper } from '../../utils/DataHelper';

class PotionSelect extends Component {
    constructor(props) {
        super(props);
    }

  render() {
      return (
        <Multiselect options={DataHelper.getPotionData()} selectedValues={this.props.selectedValues} displayValue="name" placeholder="Potion" hidePlaceholder="true" onSelect={this.props.onSelectHandler} onRemove={this.props.onRemoveHandler}></Multiselect>
    );
  }

}

export default PotionSelect;
