import React, {useContext} from 'react';
import HeroTalents from '../heroTalents/HeroTalents'
import Inventory from '../inventory/Inventory'
import './EditBuildPage.css';
import HeroDetails from '../heroDetails/HeroDetails';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import "react-tabs/style/react-tabs.css";
import BuildOptions from './BuildOptions';
import BuildSummary from './BuildSummary';
import { AppContext } from '../../stores/Store';
import HeroSelect from '../heroSelect/HeroSelect';
import { useParams, useRouteMatch } from 'react-router';
import { auth, db, firebase } from '../../utils/Firebase';
import * as Constants from '../../data/Constants';
import history from '../../utils/History';
import { UserContext } from '../../stores/UserStore';
import { Link } from 'react-router-dom';
import BuildInformation from './BuildInformation';

function EditBuildPage() {

        const [state, updateState] = useContext(AppContext);
        const userState = useContext(UserContext);

        const { path } = useRouteMatch();
        var paths = path.split('/');

        var currentPath = paths[paths.length-1];
        console.log('current path ' + currentPath);

        if (currentPath === 'edit') {
            if (state.readonly) {
                updateState({
                    type: "UPDATE_READONLY", 
                    payload: false
                });
            }
        } 
        else if (currentPath === 'view') {
            if (!state.readonly) {
                updateState({
                    type: "UPDATE_READONLY", 
                    payload: true
                });
            }
        }
        else if (currentPath === 'create') {
            if (!state.createBuild) {
                updateState({
                    type: "UPDATE_CREATE_BUILD_STATE",
                    payload: true
                });
            }
        }

        let params = useParams();

        console.log(!userState.userId);
        console.log('params build id: ' + params.buildId);

        var root = document.getElementById('root');
        root.dataset.pageName = 'editBuildPage';

        if (params.buildId && state.buildId !== params.buildId) {
            loadBuild(params.buildId);
        }

        function loadBuild(buildId) {
            console.log('loading build id ' + buildId);
            if (state.buildId !== buildId) {
                state.buildId = buildId;
            }

            db.collection('builds').doc(buildId).get().then((build) => {
                if (!build.data()) {
                    console.log('No build found with ID ' + buildId);
                    //history.push('/build/create');

                    //update state to indicate no build available in DB
                    return;
                }

                console.log('build loaded from db:');
                console.log(build.data());

                updateState({
                    type: 'INIT_STATE_FROM_DATA', 
                    payload: build
                });
            }).catch((error) => {
                console.error('Error retrieving data from DB:', error);
            });

        }

        function handleDescriptionChanged(e) {
            updateState({
                type: "UPDATE_DESCRIPTION", 
                payload: e.currentTarget.value
            });
        }

        function handleNameChange(e) {
            updateState({
                type: "UPDATE_NAME", 
                payload: e.currentTarget.value
            });
        }

        function saveBuildClick() {
            
            if (!auth.currentUser) {
                alert('Can\'t save or create builds when not authenticated.');
                return;
            }

            if (!state.dirty) {
                console.log('Not saving build, no changes have been made.');
                return;
            }

            console.log('saving build to database build id ' + state.buildId + ' for user id ' + auth.currentUser.uid);

            if (state.buildId === 0) {
                createBuild();
            }
            else {
                saveBuild();
            }
        }

        function createBuild() {
            console.log('Creating new build.');
            let increment = 1;//firebase.firestore.FieldValue.increment(1);

            let batch = db.batch();

            let buildStatsRef = db.collection('builds').doc('stats');

            db.runTransaction((transaction) => {
                return transaction.get(buildStatsRef).then((buildStats) => {
                    if (!buildStats) {
                        console.log('No stats document found');
                        return;
                    }

                    console.log(buildStats.data());

                    var careers = buildStats.data().careers;

                    var newCareer = careers.filter((career) => {return career.id === state.careerId;});
                    console.log(newCareer);

                    if (!newCareer) {
                        newCareer = {
                            id: state.careerId,
                            count: 0,
                            weapons: []
                        }
                    }

                    newCareer.count += 1;

                    var newPrimaryWeapon = newCareer.weapons.filter((weapon) => {return weapon.id === state.primaryWeaponId;});

                    if (!newPrimaryWeapon) {
                        newPrimaryWeapon = {
                            id: state.primaryWeaponId,
                            count: 0,
                            properties: [],
                            traits: []
                        }
                    }

                    newPrimaryWeapon.count += 1;

                    var newProperty1 = newPrimaryWeapon.properties.filter((property) => {return property.id === state.properties[Constants.PRIMARY_PROPERTY1_INDEX];});

                    if (!newProperty1) {
                        newProperty1 = {
                            id: state.properties[Constants.PRIMARY_PROPERTY1_INDEX],
                            count: 0
                        }
                    }

                    newProperty1.count += 1;

                    var newProperty2 = newPrimaryWeapon.properties.filter((property) => {return property.id === state.properties[Constants.PRIMARY_PROPERTY2_INDEX];});

                    if (!newProperty2) {
                        newProperty2 = {
                            id: state.properties[Constants.PRIMARY_PROPERT2_INDEX],
                            count: 0
                        }
                    }

                    newProperty2.count += 1;

                    var newTrait = newPrimaryWeapon.properties.filter((property) => {return trait.id === state.traits[Constants.PRIMARY_TRAIT_INDEX];});

                    if (!newTrait) {
                        newTrait = {
                            id: state.properties[Constants.PRIMARY_PROPERT2_INDEX],
                            count: 0
                        }
                    }

                    newTrait.count += 1;

                    var newTraitIndex = newPrimaryWeapon.traits.findIndex((trait) => { return trait.id === newTrait.id});
                    if (newTraitIndex < 0) {
                        newPrimaryWeapon.traits.push(newTrait);
                    }
                    else {
                        newPrimaryWeapon.traits[newTraitIndex] = newTrait;
                    }

                    var newProperty1Index = newPrimaryWeapon.properties.findIndex((property) => { return property.id === newProperty1.id});
                    if (newProperty1Index < 0) {
                        newPrimaryWeapon.properties.push(newProperty1);
                    }
                    else {
                        newPrimaryWeapon.properties[newProperty1Index] = newProperty1;
                    }

                    var newProperty2Index = newPrimaryWeapon.properties.findIndex((property) => { return property.id === newProperty2.id});
                    if (newProperty2Index < 0) {
                        newPrimaryWeapon.properties.push(newProperty2);
                    }
                    else {
                        newPrimaryWeapon.properties[newProperty2Index] = newProperty2;
                    }

                    var newPrimaryWeaponIndex = newCareer.weapons.findIndex((weapon) => { return weapon.id === newPrimaryWeapon.id});
                    if (newPrimaryWeaponIndex < 0) {
                        newCareer.weapons.push(newPrimaryWeapon);
                    }
                    else {
                        newCareer.weapons[newPrimaryWeaponIndex] = newPrimaryWeapon;
                    }

                    var newCareerIndex = careers.findIndex((career) => { return career.id === newCareer.id});
                    if (newCareerIndex < 0) {
                        careers.push(newCareer);
                    }
                    else {
                        careers[newCareerIndex] = newCareer;
                    }
                    
                    transaction.update(buildStatsRef, careers);
                });
            }).then(() => {
                console.log('successfully updated stats');
            });
/* 
            batch.update(db.collection('builds').doc('stats'), {
                careers: [
                    {
                        id: state.careerId,
                        count: increment,
                        weapons: [
                            {
                                id: state.primaryWeaponId,
                                count: increment,
                                properties: [
                                    {
                                        id: state.properties[Constants.PRIMARY_PROPERTY1_INDEX],
                                        count: increment
                                    },
                                    {
                                        id: state.properties[Constants.PRIMARY_PROPERTY2_INDEX],
                                        count: increment
                                    }
                                ],
                                traits: [
                                    {
                                        id: state.traits[Constants.PRIMARY_TRAIT_INDEX],
                                        count: increment
                                    }
                                ]
                            },
                            {
                                id: state.secondaryWeaponId,
                                count: increment,
                                properties: [
                                    {
                                        id: state.properties[Constants.SECONDARY_PROPERTY1_INDEX],
                                        count: increment
                                    },
                                    {
                                        id: state.properties[Constants.SECONDARY_PROPERTY2_INDEX],
                                        count: increment
                                    }
