import React from 'react'
import { AppContext } from './Store';
import {withRouter} from 'react-router';

const UserContext = React.createContext();

export {UserContext}

function reducer(state, action) {
    switch(action.type) {
        case 'LOGIN_USER':
            return {...state, username: action.payload.username, email: action.payload.email};
        case 'UPDATE_USER_INFO':
            console.log('updating user state ' + action.payload.userId);
            return {...state, userId: action.payload.userId, username: action.payload.username, email: action.payload.email, steamId: action.payload.steamId, twitch: action.payload.twitch};
        default:
            throw new Error('Error updating User Page state.');
    }
}

export default function UserStore(props) {

    const stateHook = React.useReducer(reducer, {
        userId: '',
        username: '',
        email: '',
        steamId: '',
        twitch: '',
        dateCreated: '',
        dateModified: ''
    });

    return (
        <UserContext.Provider value={stateHook}>
            {props.children}
        </UserContext.Provider>
    )
}
