import React from 'react';
import { Link } from 'react-router-dom';
import './MenuPage.css';

function MenuPage() {
    return (
        <div className="navigation-menu-container"> 
        <div className="nagivation-menu-button" onClick={clickNavigationMenu}><div className="nagivation-menu-overlay"></div></div>   
        <div className="navigation-menu-page">    
            <div className="menu-title-label"></div>      
            <div className="menu-title-divider divider-03"></div>      
            <ul className="app-navigation-container">
              <li className="app-navigation-button border-04"><Link to="/builds" data-pagename="buildsPage" onClick={loadPage.bind()}>Builds</Link></li>
              <li className="app-navigation-button border-04"><Link to="/heroes" data-pagename="heroesPage" onClick={loadPage.bind()}>Heroes</Link></li>
              <li className="app-navigation-button border-04"><Link to="/login" data-pagename="loginPage" onClick={loadPage.bind()}>Login</Link></li>
              <li className="app-navigation-button border-04"><Link to="/about" data-pagename="aboutPage" onClick={loadPage.bind()}>About</Link></li>
            </ul>
        </div>
        </div>
    );
  }

function loadPage(pageName) {
    console.log('loading page');
    var root = document.getElementById('root');
    console.log(root);
    //root.className = '';
    console.log(pageName.currentTarget.dataset.pagename);
    //root.classList.add(pageName.currentTarget.dataset.pagename);
}

function clickNavigationMenu(){
    console.log('clicked');
    var root = document.getElementById('root');

    if (root.classList.contains('showNav')) {
        root.classList.remove('showNav');
    }
    else {
        root.classList.remove('add');
    }
}

export default MenuPage;
