import React, {Component} from 'react';
import './WeaponIcon.css';

class WeaponIcon extends Component {
    constructor(props) {
        super(props);

        this.state = {
            id: props.id,
            type: props.type,
            //handleClick: props.handleClick(),
            selected: props.selected
        }
    }

  render() {
    var weaponIconClass = this.state.selected ? 'weapon-icon selected' : 'weapon-icon';
      return (
        <div key={this.state.id} className="weapon-icon-container weapon-background" data-id={this.state.id} data-type={this.state.type}>
            <div className={weaponIconClass}>
                <div className="weapon-border" data-id={this.state.id} data-type={this.state.type} onClick={this.props.handleClick()}></div>
            </div>
        </div>
    );
  }

}

export default WeaponIcon;
